static void header(Start* start)
{
	u8 val = start->ticks;
	if (val > 170) val = 0;
	if (val > 90) val = 90;

	if(!start->play && val == 90)
	{
		playSystemSfx(1);

		start->play = true;
	}

	drawHeader(start, val);
}

static void end(Start* start)
{
	if(start->play)
	{	
		start->tic->api.sfx_stop(start->tic, 0);
		start->play = false;
	}

	drawHeader(start, 0);

	setStudioMode(TIC_CONSOLE_MODE);
}

static void tick(Start* start)
{
	if(!start->initialized)
	{
		start->phase = 1;
		start->ticks = 0;

		start->initialized = true;
	}

	start->tic->api.clear(start->tic, TIC_COLOR_BG);

	static void(*const steps[])(Start*) = {header, end};

	steps[start->ticks / (TIC80_FRAMERATE * 3)](start);

	start->ticks++;
}

void initStart(Start* start, tic_mem* tic)
{
	*start = (Start)
	{
		.tic = tic,
		.initialized = false,
		.phase = 1,
		.ticks = 0,
		.tick = tick,
		.play = false,
	};
}
